-- ----------------------------
BEGIN;
INSERT INTO `media_arts` VALUES ('1', '项目简介', null, '<p>传媒类专业是我国各大高校为适应快速发展的传媒行业，面向广播、电影、电视、网络、会展、广告出版等媒体行业及企业、培养高层次专业人才开设的新兴学科。传媒艺考即通过传媒专业参加全国各大院校艺术高考，主要包括播音主持类、影视编导类、摄影摄像类、表演空乘类四大专业，与美术、音乐、体育并称高考小四科。</p>', '启用', '2017-12-14 04:11:10', '2017-12-14 04:11:10'), ('2', '播音主持', null, '<p><span style=\"color: rgb(112, 48, 160);\"><strong>专业方向</strong></span></p><p>双语播音主持（中英）、普通话播音主持、粤语播音主持、国际交流、播音配音、文化礼仪等。</p><p><br/></p><p><strong><span style=\"color: rgb(112, 48, 160);\">课程内容</span></strong></p><p>播音发声、语音校正、表达基础、形体礼仪、新闻播读、自备稿件、文学朗诵、自备稿件、即兴评述、模拟主持、现场报道、上镜练习等。</p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>报考条件</strong></span></p><p>发音器官无疾病、无色盲夜盲、形象气质好，男身高170cm以上，女160cm以上。</p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>考试内容</strong></span></p><p>自我介绍、自备稿件、指定稿件、即兴评述、命题演讲、才艺展示、模拟主持。</p>', '启用', '2017-12-14 04:48:48', '2017-12-14 04:48:48'), ('3', '影视编导', null, '<p><span style=\"color: rgb(112, 48, 160);\"><strong>专业方向</strong></span></p><p>电视编导、影视导演、文艺编导、影视文学、媒体创意、数字媒体、节目制作、艺术管理、公共事业管理、制片管理等。</p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>课程内容</strong></span></p><p>命题演讲、视听语言分析、艺术概论、命题写作及技巧、编讲故事、即兴创作与评述、文学艺术常识、中外影视作品分析、影视基础知识叙事散文、广告创意、栏目策划等。</p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>报考条件</strong></span></p><p>文字写作、文学积累、阅片积累、即兴思维、逻辑思维等，无身高要求。</p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>考试内容</strong></span></p><p>自我介绍、故事编写、文艺常识、影视作品分析、即兴评述、广告创意、节目活动策划等。\r\n &nbsp; &nbsp; &nbsp; &nbsp;</p>', '启用', '2017-12-14 05:26:31', '2017-12-14 05:28:49'), ('4', '影视表演', null, '<p><span style=\"color: rgb(112, 48, 160);\"><strong>专业方向</strong></span></p><p>影视表演、话剧表演、时装表演、模特表演等。</p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>课程内容</strong></span></p><p>朗诵、声乐、表演、形体等。</p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>报考条件</strong></span></p><p>长相、身高、即兴思维、才艺、舞台经验、语言表达等，五官端正、举止大方得体，男170cm以上，女160cm以上。</p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>考试内容</strong></span></p><p>自我介绍、声音、台词、形体、表演（单人或多人表演）、才艺展示、小品创作等。</p>', '启用', '2017-12-14 05:34:59', '2017-12-14 05:34:59'), ('5', '摄影摄像', null, '<p><span style=\"color: rgb(112, 48, 160);\"><strong>专业方向</strong></span></p><p>摄影摄像、图片摄影、舞台照明艺术、数字媒体艺术、影视制作等。</p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>课程内容</strong></span></p><p>文学艺术常识、摄影基础、摄影作品赏析、新闻事件分析与阐述、命题故事、叙事写作、模拟现场拍摄等。</p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>报考条件</strong></span></p><p>无色盲色弱、矫正视力4.8以上、无平足、男身高170cm以上、女160cm以上。</p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>考试内容</strong></span></p><p>文学常识、摄影基础知识、摄影作品分析、新闻事件分析与阐述、命题故事、叙事写作、模拟现场拍摄等。</p>', '启用', '2017-12-14 05:37:25', '2017-12-14 05:37:25'), ('6', '可考院校', null, '<p><span style=\"color: rgb(112, 48, 160);\"><strong>全国目前开设出传媒类专业的院校多达300多所并逐年增加：</strong></span></p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>著名传媒院校</strong></span></p><p>中国传媒大学、北京电影学院、中央戏剧学院、上海戏剧学院、中国戏曲学院、南京艺术学院、浙江传媒学院等。</p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>广东省内院校</strong></span></p><p>暨南大学、华南农业大学、广东外语外贸大学、广州大学、深圳大学、广州体育学院、广东财经大学、广东海洋大学、北京师范大学珠海分校等。</p><p><br/></p><p><span style=\"color: rgb(112, 48, 160);\"><strong>综合类院校</strong></span></p><p>北京师范大学、武汉大学、重庆大学、南京大学、湖南大学、四川大学、吉林大学等。</p>', '启用', '2017-12-14 05:39:29', '2017-12-14 05:39:29');
COMMIT;

